import React from "react"

export class Header extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        return(
            <header class='shadow'>
                <div class="row">
                    <div class="name">
                        <h1>SHOPPER</h1>
                        <h5>Sklep internetowy</h5> 
                    </div>
                </div>
            </header>
        );
    }
}

export class Strona extends React.Component{
    render(){
        const oferty = [
            {
                id:1,
                tytul:"Arthas - Przebudzenie Króla Lisza",
                autor:"Christie golden",
                liczbaStron: 365,
                gatunek: "Fantasy",
                uniwersum: "Warcraft"
    
            },
            {
                id:2,
                tytul:"Illidan",
                autor:"William King",
                liczbaStron: 414,
                gatunek: "Fantasy",
                uniwersum: "Warcraft"
            },
            {
                id:3,
                tytul:"Fale ciemności",
                autor:"Aaron Rosenberg",
                liczbaStron: 324,
                gatunek: "Fantasy",
                uniwersum: "Warcraft"
            }
        ];
        return(
            <div class="w-75 mx-auto">
                 <Oferta lista={oferty}></Oferta>
            </div>
        );
    }
}


export class Oferta extends React.Component{
    state = {
        id: this.props.lista.id
    }
    render(){
        return(
            <div>
                {this.props.lista.map(function(dane){
                        return(
                            <div class="element shadow py-1">
                                <div class="row">
                                    <div class="col-md-3">OBRAZEK</div>
                                    <div class="col-md-9">
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

function Opis(props) {  
    const myLists = props.myLists;  
    const listItems = myLists.map((myList) =>  
      <li>{myList}</li>  
    );  
    return (  
      <div>  
            <h3>React Map Example</h3>  
            <ul>{listItems}</ul>  
      </div>  
    );  
  }
