import React from "react"

export class Header extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        return(
            <div>SKLEP
            </div>
        );
    }
}

export class Strona extends React.Component{
    render(){
        const BOOKS = [
            {
                id:1,
                tytul:"Arthas - Przebudzenie Króla Lisza",
                autor:"Christie golden",
                liczbaStron: 365,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
    
            },
            {
                id:2,
                tytul:"Illidan",
                autor:"William King",
                liczbaStron: 414,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
            },
            {
                id:3,
                tytul:"Fale ciemności",
                autor:"Aaron Rosenberg",
                liczbaStron: 324,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
            }
        ];
        return(
            <div>
                 <Oferta books={BOOKS}></Oferta>
                 <ChildImg images={BOOKS}></ChildImg>
            </div>
           
        );
    }
}


export class Oferta extends React.Component{
    state = {
        x: this.props.images[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.images.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <div class="row">
                                    <div class="col-md-8">.col-md-8</div>
                                    <div class="col-md-4">.col-md-4</div>
                                </div>
                            </div>
                                
                        )
                    })
                }
            </div>
        )
    }
}
