import React from "react"

export class Header extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        return(
            <header class='shadow'>
                <div class="row">
                    <div class="name">
                        <h1>SHOPPER</h1>
                        <h5>Sklep internetowy</h5> 
                    </div>
                </div>
            </header>
        );
    }
}

export class Strona extends React.Component{
    render(){
        const oferty = ['A', 'B', 'C', 'D', 'D'];
        return(
            <div class="w-75 mx-auto">
                 <Oferta lista={oferty}></Oferta>
            </div>
        );
    }
}


export class Oferta extends React.Component{
    state = {
        id: this.props.lista.id
    }
    render(){
        return(
            <div class="element shadow">
                <div class="row">
                    <span>{this.state.id}</span>
                    {this.props.lista.map(function(dane){
                            return(
                                <div>
                                    <div class="col-md-3">OBRAZEK</div>
                                    <div class="col-md-9">
                                        <Opis></Opis>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}

function Opis(props) {  
    const myLists = props.myLists;  
    const listItems = myLists.map((myList) =>  
      <li>{myList}</li>  
    );  
    return (  
      <div>  
            <h3>React Map Example</h3>  
            <ul>{listItems}</ul>  
      </div>  
    );  
  }
