import React from "react"

export class Header extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        return(
            <header class='shadow'>
                <div class="row">
                    <div class="name">
                        <h1>SHOPPER</h1>
                        <h5>Sklep internetowy</h5> 
                    </div>
                </div>
            </header>
        );
    }
}

export class Strona extends React.Component{
    render(){
        const oferty = [
            {
                id:1,
                ikona:false,
                nazwa:"Arthas - Przebudzenie Króla Lisza",
                opis:"Christie golden",
                cena: 365
    
            },
            {
                id:2,
                ikona:false,
                nazwa:"Arthas - Przebudzenie Króla Lisza",
                opis:"Christie golden",
                cena: 365
            },
            {
                id:3,
                ikona:false,
                nazwa:"Arthas - Przebudzenie Króla Lisza",
                opis:"Christie golden",
                cena: 365
            },
            {
                id:4,
                ikona:false,
                nazwa:"Arthas - Przebudzenie Króla Lisza",
                opis:"Christie golden",
                cena: 365
            },
            {
                id:5,
                ikona:false,
                nazwa:"Arthas - Przebudzenie Króla Lisza",
                opis:"Christie golden",
                cena: 365
            }
        ];
        return(
            <div class="w-75 mx-auto">
                 <Oferta lista={oferty}></Oferta>
            </div>
        );
    }
}


export class Oferta extends React.Component{
    render(){
        return(
            <div>
                {this.props.lista.map(function(dane){
                        return(
                            <div class="element shadow mb-5">
                                <div class="row">
                                    <div class="col-md-3"><img src={dane.ikona} alt={dane.id}/></div>
                                    <div class="col-md-9">
                                        <Opis informacje={dane}></Opis>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

function Opis(informacje) {  
    return (  
      <div>  
            <h3>React Map Example</h3>  
            <ul>{informacje.nazwa}</ul>  
      </div>  
    );  
  }
