import './App.css';
import FunctionalComp from './components/FunctionalComponent'
import React from 'react'
import arthas from './images/arthas.jpg';
import illidan from './images/illidan.jpg';
import fale from './images/fale ciemnosci.jpg';

function App() {
    return (
        <div>
            <Parent></Parent>
            <FunctionalComp></FunctionalComp>
        </div>      
    );

}


class Parent extends React.Component{
    render(){
        const BOOKS = [
            {
                id:1,
                tytul:"Arthas - Przebudzenie Króla Lisza",
                autor:"Christie golden",
                liczbaStron: 365,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: arthas

            },
            {
                id:2,
                tytul:"Illidan",
                autor:"William King",
                liczbaStron: 414,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: illidan
            },
            {
                id:3,
                tytul:"Fale ciemności",
                autor:"Aaron Rosenberg",
                liczbaStron: 324,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
                image: fale
            }
        ];
        return(
            <div>
                 <Child books={BOOKS}></Child>
                 <ChildImg images={BOOKS}></ChildImg>
            </div>

        );
    }
}


class Child extends React.Component{
    state = {
        x: this.props.books[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.books.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <span>Tytuł książki: {Book.tytul} </span>
                                <br></br>
                                <span>Autor: {Book.autor} </span>
                                <br></br>
                                <span>Gatunek: {Book.gatunek} </span>
                                <br></br>
                                <span>Uniwersum: {Book.uniwersum} </span>
                                <br></br>
                                <span>Liczba stron: {Book.liczbaStron} </span>
                            </div>

                        )
                    })
                }
            </div>
        )
    }
}

class ChildImg extends React.Component{
    state = {
        x: this.props.images[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.images.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <img src={Book.image} alt="logo" />
                            </div>

                        )
                    })
                }
            </div>
        )
    }
}




export default App;
