import React from "react"


export class Parent extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        const BOOKS = [
            {
                id:1,
                tytul:"Arthas - Przebudzenie Króla Lisza",
                autor:"Christie golden",
                liczbaStron: 365,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
    
            },
            {
                id:2,
                tytul:"Illidan",
                autor:"William King",
                liczbaStron: 414,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
            },
            {
                id:3,
                tytul:"Fale ciemności",
                autor:"Aaron Rosenberg",
                liczbaStron: 324,
                gatunek: "Fantasy",
                uniwersum: "Warcraft",
            }
        ];
        return(
            <div>
                 <Child books={BOOKS}></Child>
                 <ChildImg images={BOOKS}></ChildImg>
            </div>
           
        );
    }
}


export class Child extends React.Component{
    state = {
        x: this.props.books[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.books.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <span>Tytuł książki: {Book.tytul} </span>
                                <br></br>
                                <span>Autor: {Book.autor} </span>
                                <br></br>
                                <span>Gatunek: {Book.gatunek} </span>
                                <br></br>
                                <span>Uniwersum: {Book.uniwersum} </span>
                                <br></br>
                                <span>Liczba stron: {Book.liczbaStron} </span>
                            </div>
                                
                        )
                    })
                }
            </div>
        )
    }
}

export class ChildImg extends React.Component{
    state = {
        x: this.props.images[0].id
        // x:'test'
    }
    render(){
        return(
            <div>
                <span>{this.state.x}</span>
                {this.props.images.map(function(Book){
                        return(
                            <div key={Book.id}>
                                <div class="row">
                                    <div class="col-md-8">.col-md-8</div>
                                    <div class="col-md-4">.col-md-4</div>
                                </div>
                            </div>
                                
                        )
                    })
                }
            </div>
        )
    }
}
    
export default ClassComp;
