import React from "react"

import samsung from "./images/samsung_galaxy_a20s.png"

export class Header extends React.Component{
    componentDidMount(){
        document.title = "Patryk Morawski 185IC B1 - Laboratorium 6"
    }
    render(){
        return(
            <header class='shadow'>
                <div class="row">
                    <div class="name">
                        <h1>SHOPPER</h1>
                        <h5>Sklep internetowy</h5> 
                    </div>
                </div>
            </header>
        );
    }
}
export class Footer extends React.Component{
    render(){
        return(
            <footer>
                Copyright © Patryk Morawski 2020
            </footer>
        );
    }
}

export class Strona extends React.Component{
    render(){
        const oferty = [
            {
                id:1,
                ikona:samsung,
                nazwa:"Samsung Galaxy A20s",
                cena: 699.99
    
            },
            {
                id:2,
                ikona:samsung,
                nazwa:"Samsung Galaxy A20s",
                cena: 699.99
            },
            {
                id:3,
                ikona:samsung,
                nazwa:"Samsung Galaxy A20s",
                cena: 699.99
            },
            {
                id:4,
                ikona:samsung,
                nazwa:"Samsung Galaxy A20s",
                cena: 699.99
            },
            {
                id:5,
                ikona:samsung,
                nazwa:"Samsung Galaxy A20s",
                cena: 699.99
            }
        ];
        return(
            <div class="w-75 mx-auto">
                 <Oferta lista={oferty}></Oferta>
            </div>
        );
    }
}


export class Oferta extends React.Component{
    render(){
        return(
            <div>
                {this.props.lista.map(function(dane){
                        return(
                            <div class="element shadow mb-5">
                                <div class="row">
                                    <div class="col-md-3 text-center">
                                        <img class="show img-fluid" src={dane.ikona} alt={dane.nazwa}/>
                                    </div>
                                    <div class="col-md-9 p-2">
                                        <Opis informacje={dane}></Opis>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

function Opis(info) {  
    return (  
      <div>  
            <h6>{info.informacje.nazwa}</h6>   
            <span>{info.informacje.cena} zł</span> 
      </div>  
    );  
  }
