import React from 'react'
import {Header, Strona} from './components/ClassComponent'

function App() {
    const [darkTheme, setDarkTheme] = React.useState(false)
    return (
        <div class={darkTheme ? 'dark-theme' : 'light-theme'}>
            <Header>
                <button onClick={() => setDarkTheme(prevTheme => !prevTheme)}>
                    Zmiana motywu
                </button>
            </Header>
            <Strona></Strona>
        </div>      
    );

}

export default App;
